from django.contrib import adminfrom overtime_app.models import Holiday, Employee, OvertimeRequest, ApprovalLog, Position@admin.register(Position)class PositionAdmin(admin.ModelAdmin):    list_display = ['name']@admin.register(Holiday)class HolidayAdmin(admin.ModelAdmin):    list_display = ['date', 'is_holiday']@admin.register(Employee)class EmployeeAdmin(admin.ModelAdmin):    list_display = ['full_name', 'department', 'position', 'is_manager']    list_filter = ['position']@admin.register(OvertimeRequest)class OvertimeRequestAdmin(admin.ModelAdmin):    list_display = ['id', 'employee', 'start_datetime', 'end_datetime', 'hours', 'status', 'reason']    list_filter = ['status', 'reason', 'employee']@admin.register(ApprovalLog)class ApprovalLogAdmin(admin.ModelAdmin):    list_display = ['request', 'approver', 'decision', 'timestamp']