from django.contrib import adminfrom overtime_app.models import Holiday, Employee, OvertimeRequest, ApprovalLog, Position@admin.register(Position)class PositionAdmin(admin.ModelAdmin):    list_display = ['name']@admin.register(Holiday)class HolidayAdmin(admin.ModelAdmin):    list_display = ['date', 'is_holiday']@admin.register(Employee)class EmployeeAdmin(admin.ModelAdmin):    list_display = ['full_name', 'department', 'job_title_manual', 'role_type', 'email']    list_filter = ['department', 'role_type']    search_fields = ['full_name', 'email', 'job_title_manual']@admin.register(OvertimeRequest)class OvertimeRequestAdmin(admin.ModelAdmin):    list_display = ['id', 'employee', 'start_datetime', 'end_datetime', 'hours', 'status', 'reason']    list_filter = ['status', 'reason', 'employee__department', 'employee']    search_fields = ['employee__full_name', 'justification']@admin.register(ApprovalLog)class ApprovalLogAdmin(admin.ModelAdmin):    list_display = ['request', 'approver', 'decision', 'timestamp']    search_fields = ['request__id', 'approver__full_name']    list_filter = ['decision', 'timestamp']