from django.urls import pathfrom . import viewsapp_name = 'overtime_app'urlpatterns = [    path('', views.dashboard, name='dashboard'),    path('create/', views.create_request, name='create_request'),    path('my-requests/', views.my_requests, name='my_requests'),    path('requests/', views.view_requests, name='view_requests'),    path('request/<int:req_id>/change-status/', views.change_status, name='change_status'),    path('approve/<int:req_id>/', views.approve_via_email, name='approve_via_email'),    path('export/', views.export_requests, name='export_requests'),    path('export/word/', views.export_requests_word, name='export_requests_word'),    path('request-access/', views.request_access, name='request_access'),    path('reset-password/', views.reset_password, name='reset_password'),    path('api/request/<int:request_id>/', views.get_request_data, name='get_request_data'),    path('lk/request/<int:request_id>/edit/', views.update_request, name='update_request'),]